import React,{ useEffect, useState } from "react";
import { useHistory } from "react-router";

const Home=()=>{
     console.log('in home');
    const history=useHistory();
    const [userData,setUserData]=useState({});

    useEffect(()=>{
        callHome();
        // PostData();
    },[]);
    
    const callHome= async ()=>{
        try {
            const res=await fetch('/todos/home',{
                method:'GET',
                headers:{
                    
                    Accept:"application/json",
                    "Content-Type":"application/json"

                },
                credentials:"include"
            });
            const data= await res.json();
            console.log(data);
            var today = new Date();
            var dd = String(today.getDate()).padStart(2, '0');
            var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
            var yyyy = today.getFullYear();

            today =  yyyy+ '-' + mm + '-' + dd ;
            console.log(today);
            setUserData(data);
        } catch (error) {
            console.log(error);
        }
    }

//     const PostData= async (e)=>{
        
//     const res=await fetch('/todos/fetchmeal',{
//         method:"POST",
//         headers:{
//             "Content-Type":"application/json"
//         },
//         body:JSON.stringify({
//             date:new Date().toLocaleString() + ''
//         })
//     });

//     const data=await res.json();
//     if (data.error) {
//         alert(data.success);
//     } else {
//         console.log("in home mealsfetched");
//         // history.push('/home');
//     }
    
// }


    return(
        <div className="user" >
            <h1 class="user__title">hello { userData.email}</h1>
            <p>Max calories allowed: {userData.maxcal}</p>
        <br></br>
        <a href="/todos/createmeal">Create Meal </a><br></br>
        <a href="/todos/updatemeal">Update Meal</a><br></br>
        <a href="/todos/deletemeal">Delete Meal</a><br></br>
        <a href="/todos/fetchmeal">fetch Meal</a><br></br>
        </div>
        )

}
export default Home;
